def grade(ns):
  max_score=100
  if 'safe_divide' not in ns: return {'score':0,'max_score':max_score,'feedback':'Function missing'}
  f=ns['safe_divide']
  try:
    if f(6,3)!=2: return {'score':40,'max_score':max_score,'feedback':'6/3 should be 2'}
    if f(1,0)!=0: return {'score':60,'max_score':max_score,'feedback':'Default when b==0 should be used'}
    if f(1,0,default=99)!=99: return {'score':80,'max_score':max_score,'feedback':'Keyword default not applied'}
    return {'score':100,'max_score':max_score,'feedback':'Great!'}
  except Exception as e:
    return {'score':0,'max_score':max_score,'feedback':str(e)}

