def grade(ns):
  max_score=100
  if 'echo_signature' not in ns: return {'score':0,'max_score':max_score,'feedback':'Function missing'}
  f=ns['echo_signature']
  p,k=f(1,2,a=3,b=4)
  if p!=[1,2]: return {'score':50,'max_score':max_score,'feedback':'Positional args wrong'}
  if k!=[('a',3),('b',4)]: return {'score':80,'max_score':max_score,'feedback':'Keyword sorting wrong'}
  return {'score':100,'max_score':max_score,'feedback':'Nice!'}
